var express=require("express");
var app = express();
var fs = require("fs");

app.use(function (req,res,next) {
    req.money = 10000;
    console.log('中央');
    next();
})
app.use(function (req,res,next) {
    req.money = req.money - 3000;
    console.log('省');
    next();
})
app.use(function (req,res,next) {
    req.money = req.money - 2000;
    console.log('市');
    next();
})
app.use(function (req,res,next) {
    req.money = req.money - 1000;
    console.log('县');
    next();
})
app.use(function (req,res,next) {
    req.money = req.money - 1000;
    console.log('乡');
    next();
})
app.use(function (req,res,next) {
    req.money = req.money - 500;
    console.log('村');
    next();
})
app.use(function (req,res) {
    console.log('农民');
    res.send(req.money + "");
})

app.listen(3000);